package org.hippoecm.frontend.plugins.cms.admin.updater

import org.onehippo.repository.update.BaseNodeUpdateVisitor
import javax.jcr.Node

class UpdaterTemplate extends BaseNodeUpdateVisitor {

    boolean doUpdate(Node node) {

        if(node.getPath().contains("jcr:system")){
            return false;
        }

        if(!node.getPrimaryNodeType().getName().equals("hst:containercomponent")){
            return false
        }
        node.setProperty("hst:template", "custom-no-markup")
            if(node.hasProperty("hst:xtype")){
                log.debug "Updating node ${node.path}"
                return updateValue(node)
            }
        return false
    }

    boolean updateValue(Node node){
        node.setProperty("hst:xtype", "hst.vbox")
    }

    boolean undoUpdate(Node node) {
        throw new UnsupportedOperationException('Updater does not implement undoUpdate method')
    }

}
